import React, { useState } from 'react'
import quizData from './Data'
import Result from './Result';
import './Game.css'
// import bgImage from './bg.jpg';


const Quizegame = () => {
    const [nextquestion,setnextquestion]=useState(0);
    const [score,setscore]=useState(0);
    const [result,setresult]=useState(0)
    function handleans(incorrect){
if(incorrect){
setscore(score+1);
}
    }
    function save(){
let newquestion=nextquestion+1;
if(newquestion<quizData.length){
    setnextquestion(newquestion);
}else{
setresult(true);
}

    }
  return (
    <>
     <div style={{backgroundImage:`url(${bg})`}} className='bg-image'>
 {result?<Result score={score} length={quizData.length}/>:(<div className ="main height-one d-flex justify-content-center">
        <div className="width">
          <h1 className="text-center border py-2 box">QUIZE GAME</h1>
        <div><h3>Select the correct option</h3></div>
        <h5 id="questions">{quizData[nextquestion].question}</h5>
        <div className="ps-3">
            {quizData[nextquestion].answer.map((value,index)=>{
                return <div key={index}> <input type="radio" name="answer" id="ans1" className="answers"onClick={()=>handleans(value.incorrect)}/>
                <label id="option1" for="answer1">{value.answerText}</label>
              </div>
            })}
        </div>
        <div>
          <button className="bg-danger px-4 py-2 text-white btn" onClick={save}>Submit</button>
      </div>
      <div  className="res ult mx-5 my-4 d-flex justify-content-center align-items-center" >
      </div>
      </div>
      </div>
 )}
    </div>
    </>

  )
}

export default Quizegame
